#define minCutNodeSetNext(nodeInfoArray,node,next) \
{\
  int j;\
  nodeInfoArray[node].pathNext = next;\
  for (j=0; j<nodeInfoArray[node].foCnt; j++) {\
    if (nodeInfoArray[node].foIds[j] == next) {\
      nodeInfoArray[node].isPathNext[j] = 1;\
    }\
  }\
}


