        int odc = nodes[nodeId(cs[i0])].isOdc;
        int odc_i = abs(odc)-1;
        bAig_RecursiveDeref(bMgr,rBaig);
        rBaig = (odc>0) ? bAig_One : bAig_Zero;
        if (invId(cs[i0])) rBaig = bAig_Not(rBaig);
