      Ddi_BddarrayInsertLast(benchArray, Ddi_BddReadComposeF(b));
      Ddi_BddarrayAppend(benchArray,litsAndSubst);
      Ddi_BddarrayAppend(benchArray,Ddi_BddReadComposeSubst(b));
      Ddi_Free(litsAndSubst);
